import { useState } from "react";
import { Cards } from "react-bootstrap";
import { switchedCards } from "react-bootstrap";
import { cardTools } from "react-bootstrap";
import { OverlayCardTrigger} from "react-bootstrap";
import ReactCardFlipper from "react-card-flip";

const flipperCards = ({ rmCharacters }) => {
  const [flipperCards, setFlipper] = useState(false);


  const rendercardTools = (props) => (
    <cardTools {...props}>Click here for Info/Stats</cardTools>
  );

  console.log(rmCharacters);

  return (
    <switchedCards key={rmCharacters.card}>
      {}
      <style type="text/css">
        {`

    #enlarge:hover {
    transform: translateY(-10px);
    box-shadow: 0 20px 26px rgba(0,0,0,0.20);
    transform: scale(1.1);
     }

   .card {
    margin-bottom: 25px;
    margin-top: 25px;
    }


  `}
      </style>
      {}
      <ReactCardFlipper isFlipped={flipperCards} flipDirection="horizontal">
        <div className="react-cards">
          {}
          <OverlayCardTrigger
            placement="right"
            delay={{ show: 250, hide: 400 }}
            overlay={rendercardTools}
          >
            {}
            <Cards
              id="enlarge"
              style={{ width: "18rem" }}
              onClick={() => setFlipper(!flipperCards)}
              bg="warning"
            >
              <Cards.Header>
                <h2>{rmCharacters.name}</h2>{" "}
              </Cards.Header>
              <Cards.Img variant="top" src={rmCharacters.image} />
              <Cards.Body>
                <Cards.Title>{rmCharacters.name}</Cards.Title>
                <Cards.Text>
                  Gender: {rmCharacters.gender} <br />
                  Species: {rmCharacters.species} <br />
                  Status: {rmCharacters.status}
                </Cards.Text>
              </Cards.Body>
            </Cards>
          </OverlayCardTrigger>
        </div>
        {}
        <div>
          <div class="react-cards">
            <Cards
              id="enlarge"
              style={{ width: "18rem" }}
              onClick={() => setFlipper(!flipperCards)}
              bg="info"
            >
              <Cards.Header>
                <h2>{rmCharacters.name}</h2>{" "}
              </Cards.Header>
              <Cards.Img variant="top" src={rmCharacters.image} />
              <Cards.Title>Stats - {rmCharacters.name}</Cards.Title>
              Episodes {rmCharacters.episode.length}
              <br />
              Location {rmCharacters.location.name}
              <br />
              Origin {rmCharacters.origin.name}
            </Cards>
          </div>
        </div>
      </ReactCardFlipper>
    </switchedCards>
  );
};

export default flipperCards;
