import { useState } from "react";
import { Cards } from "react-bootstrap";
import {crumbleCard} from "react-bootstrap";
import {SwitchedCards} from "react-bootstrap";


const crumbleCards = ({rmCharacters}) =>{


  const [open, setOpen] = useState(false);


    return (
      <SwitchedCards key={rmCharacters.card}>
        {}
        <style type="text/css">
          {`
    #enlarge:hover {
    transform: translateY(-10px);
    box-shadow: 0 20px 26px rgba(0,0,0,0.20);
    transform: scale(1.1);

     }

   .card {
    margin-bottom: 25px;
    margin-top: 25px;
    display: flex;

     }
  `}
        </style>
        <div className="react-cards">
          {}
          <Cards
            id="enlarge"
            onClick={() => setOpen(!open)}
            aria-controls="cartoon-collapse-text"
            aria-expanded={open}
            className="cards"
            bg="warning"
            style={{ width: "18rem" }}
          >
            <Cards.Header>
              <h2>{rmCharacters.name}</h2>{" "}
            </Cards.Header>
            <Cards.Img variant="top" src={rmCharacters.image} />
            <Cards.Body bg="light">
              <Cards.Title>{rmCharacters.name}</Cards.Title>
              <Cards.Text>
                Gender: {rmCharacters.gender} <br />
                Species: {rmCharacters.species} <br />
                Status: {rmCharacters.status}
              </Cards.Text>
            </Cards.Body>
          </Cards>
        </div>
        {/* collapsable card */}
        <div style={{ minHeight: "150px", width: "10rem" }}>
          <SwitchedCards in={open} dimension="width">
            <div id="example-collapse-text">
              <Cards id="enlarge" body style={{ width: "285px" }}>
                Episodes {rmCharacters.episode.length}
                <br />
                Location {rmCharacters.location.name}
                <br />
                Location {rmCharacters.origin.name}
              </Cards>
            </div>
          </SwitchedCards>
        </div>
      </SwitchedCards>
    );

}


export default crumbleCards
