import React, { useState } from 'react'

export default function CardInfo({ character }) {

    const [isFlipped, setIsFlipped] = useState(false);

    const flipCard = () => {
        setIsFlipped(!isFlipped);
    }

    return (

        <div className="cardInfo" onClick={flipCard}>
            {!isFlipped ? (
                <div className={"cardInfo--front"}>
                    <img className="cardInfo--image" src={character.image} alt={character.name} />
                    <div className="cardInfo--content">
                        <h2 className="cardInfo--title">{character.name}</h2>
                        <p className="cardInfo--text"><strong>Status:</strong> {character.status}</p>
                        <p className="cardInfo--text"><strong>Species:</strong> {character.species}</p>
                        {character.type && <p className="card--text"><strong>Type:</strong> {character.type}</p>}

                        {character.gender && <p className="card--text"><strong>Gender:</strong> {character.gender}</p>}
                    </div>
                    <div className="cardInfo--footer">
                        <p className="cardInfo--footer-text"><strong>Episodes:</strong> {character.episode.length}</p>
                    </div>
                </div>

            ) : (
                <div className={"cardInfo--back"}>
                    <div className="cardInfo--back-content">
                        <h2 className="cardInfo--back-title">{character.name}</h2>
                        <p className="cardInfo--back-text"><strong>Location:</strong> {character.location.name}</p>
                        <p className="cardInfo--back-text"><strong>Origin:</strong> {character.origin.name}</p>
                    </div>
                </div>
            )}
        </div>

    )
}
