import React, { useState, useEffect, useCallback } from 'react'

export default function CardPage ({ characters, setCharacters }) {

    const [page, setPage] = useState(1);

    // wrap setCharacters in useCallback and pass it as a dependency
    const getCharacters = useCallback(() => {
      fetch(`https://rickandmortyapi.com/api/character/?page=${page}`)
        .then((response) => response.json())
        .then((data) => setCharacters(data.results));
    }, [setCharacters, page]);

    useEffect(() => {
      getCharacters();
    }, [getCharacters]); // pass getCharacters as a dependency


    return (
        <div className="CardPage">
            <button className="CardPage--button" onClick={() => setPage(page - 1)} disabled={page === 1}>Previous</button>
            <p className="CardPage--page">Page {page}</p>
            <button className="CardPage--button" onClick={() => setPage(page + 1)} disabled={page === 42}>Next</button>
        </div>
    )
}
